<?php
 error_reporting(E_ALL); ini_set('display_errors', TRUE); ini_set('display_startup_errors', TRUE); date_default_timezone_set('Europe/London'); define('EOL',(PHP_SAPI == 'cli') ? PHP_EOL : '<br />'); date_default_timezone_set('Europe/London'); require_once dirname(__FILE__) . '/../Classes/PHPExcel.php'; $objPHPExcel = new PHPExcel(); $objWorksheet = $objPHPExcel->getActiveSheet(); $objWorksheet->fromArray( array( array('', 2010, 2011, 2012), array('Jan', 47, 45, 71), array('Feb', 56, 73, 86), array('Mar', 52, 61, 69), array('Apr', 40, 52, 60), array('May', 42, 55, 71), array('Jun', 58, 63, 76), array('Jul', 53, 61, 89), array('Aug', 46, 69, 85), array('Sep', 62, 75, 81), array('Oct', 51, 70, 96), array('Nov', 55, 66, 89), array('Dec', 68, 62, 0), ) ); $dataseriesLabels = array( new PHPExcel_Chart_DataSeriesValues('String', 'Worksheet!$C$1', NULL, 1), new PHPExcel_Chart_DataSeriesValues('String', 'Worksheet!$D$1', NULL, 1), ); $xAxisTickValues = array( new PHPExcel_Chart_DataSeriesValues('String', 'Worksheet!$A$2:$A$13', NULL, 12), new PHPExcel_Chart_DataSeriesValues('String', 'Worksheet!$A$2:$A$13', NULL, 12), ); $dataSeriesValues = array( new PHPExcel_Chart_DataSeriesValues('Number', 'Worksheet!$C$2:$C$13', NULL, 12), new PHPExcel_Chart_DataSeriesValues('Number', 'Worksheet!$D$2:$D$13', NULL, 12), ); $series = new PHPExcel_Chart_DataSeries( PHPExcel_Chart_DataSeries::TYPE_RADARCHART, NULL, range(0, count($dataSeriesValues)-1), $dataseriesLabels, $xAxisTickValues, $dataSeriesValues, NULL, PHPExcel_Chart_DataSeries::STYLE_MARKER ); $layout = new PHPExcel_Chart_Layout(); $plotarea = new PHPExcel_Chart_PlotArea($layout, array($series)); $legend = new PHPExcel_Chart_Legend(PHPExcel_Chart_Legend::POSITION_RIGHT, NULL, false); $title = new PHPExcel_Chart_Title('Test Radar Chart'); $chart = new PHPExcel_Chart( 'chart1', $title, $legend, $plotarea, true, 0, NULL, NULL ); $chart->setTopLeftPosition('F2'); $chart->setBottomRightPosition('M15'); $objWorksheet->addChart($chart); echo date('H:i:s') , " Write to Excel2007 format" , EOL; $objWriter = PHPExcel_IOFactory::createWriter($objPHPExcel, 'Excel2007'); $objWriter->setIncludeCharts(TRUE); $objWriter->save(str_replace('.php', '.xlsx', __FILE__)); echo date('H:i:s') , " File written to " , str_replace('.php', '.xlsx', pathinfo(__FILE__, PATHINFO_BASENAME)) , EOL; echo date('H:i:s') , " Peak memory usage: " , (memory_get_peak_usage(true) / 1024 / 1024) , " MB" , EOL; echo date('H:i:s') , " Done writing file" , EOL; echo 'File has been created in ' , getcwd() , EOL; 