blic function get_relation(){ return $this->relation; } public function set_sort($field,$order=false){ if (!$field && !$order) $this->sort_by=array(); else{ if ($order===false) $this->sort_by[] = $field; else { $order=strtolower($order)=="asc"?"ASC":"DESC"; $this->sort_by[]=array("name"=>$field,"direction" => $order); } } } public function set_filter($field,$value=false,$operation=false){ if ($value === false) array_push($this->filters,$field); else array_push($this->filters,array("name"=>$field,"value"=>$value,"operation"=>$operation)); } public function set_fieldset($value){ $this->fieldset=$value; } public function set_source($value){ if (is_string($value)) $value = trim($value); $this->source = $value; if (!$this->source) throw new Exception("Source of data can't be empty"); } public function set_limit($start,$count){ $this->start=$start; $this->count=$count; } public function set_relation($value){ $this->relation=$value; } public function parse_sql($sql, $as_is = false){ if ($as_is){ $this->fieldset = $sql; return; } $sql= preg_replace("/[ \n\t]+limit[\n\t ,0-9]*$/i","",$sql); $data = preg_split("/[ \n\t]+\\_from\\_/i",$sql,2); if (count($data)!=2) $data = preg_split("/[ \n\t]+from/i",$sql,2); $this->fieldset = preg_replace("/^[\s]*select/i","",$data[0],1); if ((count($data) == 1) || preg_match("#[ \n\r\t]union[ \n\t\r]#i", $sql)){ $this->fieldset = $sql; return; } $table_data = preg_split("/[ \n\t]+where/i",$data[1],2); if (sizeof($table_data)>1 && !preg_match("#.*group by.*#i",$table_data[1])){ $this->set_source($table_data[0]); $where_data = preg_split("/[ \n\t]+order[ ]+by/i",$table_data[1],2); $this->filters[]=$where_data[0]; if (sizeof($where_data)==1) return; $data=$where_data[1]; } else { $table_data = preg_split("/[ \n\t]+order[ ]+by/i",$data[1],2); $this->set_source($table_data[0]); if (sizeof($table_data)==1) return; $data=$table_data[1]; } if (trim($data)){ $s_data = preg_split("/\\,/",trim($data)); for ($i=0; $i < count($s_data); $i++) { $data=preg_split("/[ ]+/",trim($s_data[$i]),2); if (sizeof($data)>1) $this->set_sort($data[0],$data[1]); else $this->set_sort($data[0]); } } } } class DataConfig{ public $id; public $relation_id; public $text; public $data; public function __toString(){ $str="ID:{$this->id['db_name']}(ID:{$this->id['name']})\n"; $str.="Relation ID:{$this->relation_id['db_name']}({$this->relation_id['name']})\n"; $str.="Data:"; for ($i=0; $i<sizeof($this->text); $i++) $str.="{$this->text[$i]['db_name']}({$this->text[$i]['name']}),"; $str.="\nExtra:"; for ($i=0; $i<sizeof($this->data); $i++) $str.="{$this->data[$i]['db_name']}({$this->data[$i]['name']}),"; return $str; } public function minimize($name){ for ($i=0; $i < sizeof($this->text); $i++){ if ($this->text[$i]["db_name"]==$name || $this->text[$i]["name"]==$name){ $this->text[$i]["name"]="value"; $this->data=array($this->text[$i]); $this->text=array($this->text[$i]); return; } } throw new Exception("Incorrect dataset minimization, master field not found."); } public function limit_fields($data){ if (isset($this->full_field_list)) $this->restore_fields(); $this->full_field_list = $this->text; $this->text = array(); for ($i=0; $i < sizeof($this->full_field_list); $i++) { if (array_key_exists($this->full_field_list[$i]["name"],$data)) $this->text[] = $this->full_field_list[$i]; } } public function restore_fields(){ if (isset($this->full_field_list)) $this->text = $this->full_field_list; } public function init($id,$fields,$extra,$relation){ $this->id = $this->parse($id,false); $this->text = $this->parse($fields,true); $this->data = array_merge($this->text,$this->parse($extra,true)); $this->relation_id = $this->parse($relation,false); } private function parse($key,$mode){ if ($mode){ if (!$key) return array(); $key=explode(",",$key); for ($i=0; $i < sizeof($key); $i++) $key[$i]=$this->parse($key[$i],false); return $key; } $key=explode("(",$key); $data=array("db_name"=>trim($key[0]), "name"=>trim($key[0])); if (sizeof($key)>1) $data["name"]=substr(trim($key[1]),0,-1); return $data; } public function __construct($proto=false){ if ($proto!==false) $this->copy($proto); else { $this->text=array(); $this->data=array(); $this->id=array("name"=>"dhx_auto_id", "db_name"=>"dhx_auto_id"); $this->relation_id=array("name"=>"", "db_name"=>""); } } public function copy($proto){ $this->id = $proto->id; $this->relation_id = $proto->relation_id; $this->text = $proto->text; $this->data = $proto->data; } public function db_names_list($db){ $out=array(); if ($this->id["db_name"]) array_push($out,$db->escape_name($this->id["db_name"])); if ($this->relation_id["db_name"]) array_push($out,$db->escape_name($this->relation_id["db_name"])); for ($i=0; $i < sizeof($this->data); $i++){ if ($this->data[$i]["db_name"]!=$this->data[$i]["name"]) $out[]=$db->escape_name($this->data[$i]["db_name"])." as ".$this->data[$i]["name"]; else $out[]=$db->escape_name($this->data[$i]["db_name"]); } return $out; } public function add_field($name,$aliase=false){ if ($aliase===false) $aliase=$name; if ($this->id["db_name"]==$name || $this->relation_id["db_name"] == $name){ LogMaster::log("Field name already used as ID, be sure that it is really necessary."); } if ($this->is_field($name,$this->text)!=-1) throw new Exception('Data field already registered: '.$name); array_push($this->text,array("db_name"=>$name,"name"=>$aliase)); if ($this->is_field($name,$this->data)==-1) array_push($this->data,array("db_name"=>$name,"name"=>$aliase)); } public function remove_field($name){ $ind = $this->is_field($name); if ($ind==-1) throw new Exception('There was no such data field registered as: '.$name); array_splice($this->text,$ind,1); } public function remove_field_full($name){ $ind = $this->is_field($name); if ($ind==-1) throw new Exception('There was no such data field registered as: '.$name); array_splice($this->text,$ind,1); $ind = $this->is_field($name, $this->data); if ($ind==-1) throw new Exception('There was no such data field registered as: '.$name); arr