]))."'"; } if ($relation = $this->config->relation_id["db_name"]){ $temp_n[]=$this->escape_name($relation); $temp_v[]="'".$this->escape($data->get_value($relation))."'"; } if ($this->sequence){ $temp_n[]=$this->escape_name($this->config->id["db_name"]); $temp_v[]=$this->sequence; } $sql="INSERT INTO ".$request->get_source()."(".implode(",",$temp_n).") VALUES (".implode(",",$temp_v).")"; return $sql; } public function set_transaction_mode($mode){ if ($mode!="none" && $mode!="global" && $mode!="record") throw new Exception("Unknown transaction mode"); $this->transaction=$mode; } public function is_global_transaction(){ return $this->transaction == "global"; } public function is_record_transaction(){ return $this->transaction == "record"; } public function begin_transaction(){ $this->query("BEGIN"); } public function commit_transaction(){ $this->query("COMMIT"); } public function rollback_transaction(){ $this->query("ROLLBACK"); } abstract public function query($sql); abstract public function get_next($res); abstract public function get_new_id(); abstract public function escape($data); public function escape_name($data){ return $data; } public function tables_list() { throw new Exception("Not implemented"); } public function fields_list($table) { throw new Exception("Not implemented"); } } class ArrayDBDataWrapper extends DBDataWrapper{ public function get_next($res){ if ($res->index < sizeof($res->data)) return $res->data[$res->index++]; } public function select($sql){ if ($this->config->relation_id["db_name"] == "") { if ($sql->get_relation() == "0" || $sql->get_relation() == "") { return new ArrayQueryWrapper($this->connection); } else { return new ArrayQueryWrapper(array()); } } $relation_id = $this->config->relation_id["db_name"]; for ($i = 0; $i < count($this->connection); $i++) { $item = $this->connection[$i]; if (!isset($item[$relation_id])) continue; if ($item[$relation_id] == $sql->get_relation()) $result[] = $item; } return new ArrayQueryWrapper($result); } 